type Store = {
    currentPage: number;
    feeds: NewsFeed[];
}

type NewsFeed = {
    id: number;
    comments_count: number;
    url: string;
    user: string;
    time_ago: string;
    points: number;
    title: string;
    read?: boolean;
}

const container : HTMLElement | null = document.getElementById('root');
let ajax : XMLHttpRequest = new XMLHttpRequest();
const content = document.createElement('div');
const NEWS_URL= 'https://api.hnpwa.com/v0/news/1.json';
const CONTENT_URL = 'https://api.hnpwa.com/v0/item/@id.json';
const store: Store = {
    currentPage: 1,
    feeds: []
}

function getData(url : string) {
    ajax.open('GET', url , false);
    ajax.send();

    return JSON.parse(ajax.response);
}

function makeFeeds(feeds : string) {
    for (let i = 0; i < feeds.length; i+=1) {
        feeds[i].read = false;
    }

    return feeds;
}

function newsFeed() {
    let newsFeed = store.feeds;
    let newsList = [];

    let template = /* html */`
        <div class="bg-gray-600 min-h-screen">
            <div class="bg-white text-xl">
                <div class="mx-auto px-4">
                    <div class="flex justify-between items-center py-6">
                        <div class="flex justify-start">
                            <h1 class="font-extrabold text-xl">NEWS</h1>
                        </div>
                        <div class="items-center justify-end">
                            <a href="#/page/{{__prev_page__}}" class="text-gray-500">이전</a>
                            <a href="#/page/{{__next_page__}}" class="text-gray-500 ml-4">다음</a>
                        </div>
                    </div>
                </div>
            </div>
            <ul class="p-4 text-2xl text-gray-700">
                {{__news_feed__}}
            </ul>
        </div>
    `;

    if (newsFeed.length === 0) {
        newsFeed = store.feeds = makeFeeds(getData(NEWS_URL));
    }
    
    for(let i = (store.currentPage - 1) * 10; i < store.currentPage * 10; i += 1) {
        newsList.push( /* html */`

        <div class="mt-6 p-6 ${newsFeed[i].read ? ' bg-red-500' : 'bg-white'} rounded-lg shadow-md transition-colors duration-500 hover:bg-green-100">
            <div class="flex">
                <div class="flex-auto">
                    <a href="#/show/${newsFeed[i].id}">${newsFeed[i].title}</a>
                </div>
                <div class="text-center text-sm">
                    <div class=""w-10 text-white bg-green-300 rounded-lg px-0 py-2">${newsFeed[i].comments_count}</div>
                </div>
            </div>
            <div class="flex mt-3">
                <div class="grid grid-cols-3 text-sm text-gray-500">
                    <div><i class="fas fa-user mr-1"></i>${newsFeed[i].user}</div>
                    <div><i class="fas fa-heart mr-1"></i>${newsFeed[i].points}</div>
                    <div><i class="fas fa-clock mr-1"></i>${newsFeed[i].time_ago}</div>
                </div>
            </div>
        </div>
        `);
    }

    template = template.replace('{{__news_feed__}}', newsList.join(''));
    template = template.replace('{{__prev_page__}}', store.currentPage > 1 ? store.currentPage - 1 : 1);
    template = template.replace('{{__next_page__}}', store.currentPage ? store.currentPage + 1 : store.currentPage);

    container.innerHTML = template;
}

function newsDetail(){
    const id = location.hash.substring(7);

    const newsContent = getData(CONTENT_URL.replace('@id', id));

    let template =  /* html */`
        <div class="bg-gray-600 min-h-screen pb-8">
            <div class="bg-white text-xl">
                <div class="mx-auto px-4">
                    <div class="flex justify-between items-center py-6">
                        <div class="flex justify-start">
                            <h1 class="font-extrabold text-xl">NEWS</h1>
                        </div>
                        <div class="items-center justify-end">
                            <a href="#/page/${store.currentPage}" class="text-gray-500">
                                <i class="fa fa-times"></i>
                            </a>
                        </div>
                    </div>
                </div>
            </div>
            <div class="h-full m-6 p-4 border rounded-xl bg-white">
                <h1>${newsContent.title}</h1>
                <div class="text-gray-400 h-20">
                    ${newsContent.content}
                </div>
    
                {{__comment__}}
            </div>
        </div>
    `;

    for (let i = 0; i < store.feeds.length; i += 1 ){
        if (store.feeds[i].id === Number(id)){
            store.feeds[i].read = true;
            break;
        }
    }

    function makeComment(comments, called = 0) {
        const commentString = [];
    
        for (let i = 0; i< comments.length; i += 1) {
            commentString.push( /* html */`
                <div class="mt-4 pl-${called * 5}">
                    <div class="text-gray-400">
                        <i class="fa fa-sort-up mr-2"></i>
                        <strong>${comments[i].user}</strong> ${comments[i].time_ago}
                    </div>
                    <p class="text-gray-700">${comments[i].content}</p>
                </div>
            `);

            if (comments[i].comments.length > 0){
                commentString.push(makeComment(comments[i].comments, called + 1));
            }
        }
        return  commentString.join('');
    }
    container.innerHTML = template.replace('{{__comment__}}', makeComment(newsContent.comments));

}

function router() {
    const routePath = location.hash;

    if (routePath === '') {
        newsFeed()
    }  else if (routePath.indexOf('#/page/') >= 0) {
        store.currentPage = Number(routePath.substring(7));
        newsFeed();
    }else {
        newsDetail();
    }
}

window.addEventListener('hashchange', router);

router();
